mapper.select:
select
t.id, t.owner, t.money, t.name, t.expectedDate, cus.name as customerId, t.stage, t.type, t.source, ac.name as activityId, con.fullname as contactsId, t.createBy, t.createTime, t.editBy, t.editTime, t.description, t.contactSummary, t.nextContactTime
from tbl_tran t join tbl_contacts con on t.contactsId = con.id join tbl_activity ac on t.activityId = ac.id join tbl_customer cus on t.customerId = cus.id where t.id=#{transactionId}
